	@Override
	public String toString() {
		return this.value + " " + name();
	}


	/**
	 * Return the enum constant of this type with the specified numeric value.
	 * @param statusCode the numeric value of the enum to be returned
	 * @return the enum constant with the specified numeric value
	 * @throws IllegalArgumentException if this enum has no constant for the specified numeric value
	 */
	/**
	 * 返回具有指定数值的此类型的枚举常量。 
	 *  
	 * @param  statusCode要返回的枚举的数值
	 * @return 具有指定数值的枚举常量
	 * @throws  IllegalArgumentException如果此枚举没有指定数值的常量
	 */
	public static HttpStatus valueOf(int statusCode) {
		HttpStatus status = resolve(statusCode);
		if (status == null) {
			throw new IllegalArgumentException("No matching constant for [" + statusCode + "]");
		}
		return status;
	}

	/**
	 * Resolve the given status code to an {@code HttpStatus}, if possible.
	 * @param statusCode the HTTP status code (potentially non-standard)
	 * @return the corresponding {@code HttpStatus}, or {@code null} if not found
	 * @since 5.0
	 */
	/**
	 * 如果可能，将给定的状态代码解析为{@code  HttpStatus}。 
	 *  
	 * @param  statusCode HTTP状态代码（可能是非标准的），<
	 * @return>对应的{@code  HttpStatus}，如果未找到，则从{@code  null}开始@5.0
	 */
	@Nullable
	public static HttpStatus resolve(int statusCode) {
		for (HttpStatus status : values()) {
			if (status.value == statusCode) {
				return status;
			}
		}
		return null;
	}


	/**
	 * Enumeration of HTTP status series.
	 * <p>Retrievable via {@link HttpStatus#series()}.
	 */
	/**
	 * HTTP状态系列的枚举。 
	 *  <p>可通过{@link  HttpStatus＃series（）}检索。 
	 * 
	 */
	public enum Series {

		INFORMATIONAL(1),
		SUCCESSFUL(2),
		REDIRECTION(3),
		CLIENT_ERROR(4),
		SERVER_ERROR(5);

		private final int value;

		Series(int value) {
			this.value = value;
		}

		/**
		 * Return the integer value of this status series. Ranges from 1 to 5.
		 */
		/**
		 * 返回此状态系列的整数值。 
		 * 范围从1到5。 
		 * 
		 */
		public int value() {
			return this.value;
		}

		/**
		 * Return the enum constant of this type with the corresponding series.
		 * @param status a standard HTTP status enum value
		 * @return the enum constant of this type with the corresponding series
		 * @throws IllegalArgumentException if this enum has no corresponding constant
		 */
		/**
		 * 返回带有相应系列的此类型的枚举常量。 
		 *  
		 * @param 状态为标准的HTTP状态枚举值
		 * @return 带有相应系列的此类型的枚举常量
		 * @throws  IllegalArgumentException如果此枚举没有相应的常量
		 */
		public static Series valueOf(HttpStatus status) {
			return valueOf(status.value);
		}

		/**
		 * Return the enum constant of this type with the corresponding series.
		 * @param statusCode the HTTP status code (potentially non-standard)
		 * @return the enum constant of this type with the corresponding series
		 * @throws IllegalArgumentException if this enum has no corresponding constant
		 */
		/**
		 * 返回带有相应系列的此类型的枚举常量。 
		 *  
		 * @param  statusCode HTTP状态代码（可能是非标准的）
		 * @return 带有相应系列的此类型的枚举常量
		 * @throws  IllegalArgumentException如果此枚举没有相应的常量
		 */
		public static Series valueOf(int statusCode) {
			Series series = resolve(statusCode);
			if (series == null) {
				throw new IllegalArgumentException("No matching constant for [" + statusCode + "]");
			}
			return series;
		}

		/**
		 * Resolve the given status code to an {@code HttpStatus.Series}, if possible.
		 * @param statusCode the HTTP status code (potentially non-standard)
		 * @return the corresponding {@code Series}, or {@code null} if not found
		 * @since 5.1.3
		 */
		/**
		 * 如果可能，将给定的状态代码解析为{@code  HttpStatus.Series}。 
		 *  
		 * @param  statusCode HTTP状态代码（可能是非标准的），<
		 * @return>对应的{@code 系列}，如果未找到，则为{@code  null}，从5.1.3开始
		 */
		@Nullable
		public static Series resolve(int statusCode) {
			int seriesCode = statusCode / 100;
			for (Series series : values()) {
				if (series.value == seriesCode) {
					return series;
				}
			}
			return null;
		}
	}

